package Bucles;

public class While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Bucles While
		//vARIABL DE CONTROL : INICIALIZARLA A ALGUN VALOR EXPRESAR CONDICION DE CONTINUIDAD, MODIFICARLO
		
		//MOSTRAR POR PANTALLA LOS NUMEROS DEL 1 AL 50
		
		int i = 1;
		
		while (i <= 50) {
			System.out.print(i + " ");
		i += 1;
		}
		
		
	}

}
